package com.zymcloud.minioutil.controller;

import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import javax.servlet.ServletOutputStream;
import javax.servlet.http.HttpServletResponse;
import java.nio.charset.StandardCharsets;
import java.util.Base64;

/**
 * @author zhaoyimeng
 * @date 2021/06/15
 */
@RestController
@RequestMapping("/image")
public class ImageController {

    /**
     * 通过图片base64字符串输出图片
     * @param response
     */
    public void getImageByBase64Str (HttpServletResponse response) throws Exception{
        String base64Str = "";
        byte[] decode = Base64.getDecoder().decode(base64Str.replace("\n", "").replace("\r", "").getBytes(StandardCharsets.UTF_8));
        ServletOutputStream outputStream = response.getOutputStream();
        outputStream.write(decode);
        outputStream.flush();
        outputStream.close();
    }
}
